sudo echo "Use netfilter-persistent to save those permanently" && sudo apt -y install ipset && sudo apt -y install curl && sudo curl -o vpcblacklist.txt -N https://raw.githubusercontent.com/hungnguyennaz/proxydropper/main/vpcblacklist.txt && sudo ipset flush vpcblacklist ; sudo iptables -D INPUT -m set --match-set vpcblacklist src -j DROP ; sudo ipset destroy vpcblacklist ; sudo ipset restore -! < vpcblacklist.txt ; sudo rm vpcblacklist.txt && sudo iptables -I INPUT -m set --match-set vpcblacklist src -j DROP && sudo echo "Job done."